import { Text } from "react-native";
import { SafeAreaProvider, SafeAreaView } from "react-native-safe-area-context";
import { Alert, ScrollView, View } from "react-native-web";
import { s } from "./App.style";
import { Header } from "./components/Header/Header";
import { CardToDO } from "./components/CardToDo/CardToDo";
import { useState } from "react";
import { TabBottomMenu } from "./components/TabBottomMenu/TabBottomMenu";

export default function App() {
  const [todoList, setTodoList] = useState([
    { id: 1, title: "Walk the dog", isCompleted: true },
    { id: 2, title: "Go to the dentist", isCompleted: false },
    { id: 3, title: "Learn React Native", isCompleted: false },
    { id: 4, title: "Go to the gym", isCompleted: true },
    { id: 5, title: "Pay the bills", isCompleted: false },
    { id: 6, title: "Attend the meeting", isCompleted: false },
    { id: 7, title: "Call Family", isCompleted: true },
    { id: 8, title: "Update the link", isCompleted: false },
    { id: 9, title: "Wash the car", isCompleted: false },
  ]);

  const [selectedTabName, setSelectedTabName] = useState("all");

  function getFileteredList() {
    switch (selectedTabName) {
      case "all":
        return todoList;
      case "inProgress":
        return todoList.filter((todo) => todo.isCompleted === false);
      case "done":
        return todoList.filter((todo) => todo.isCompleted === true);
    }
  }

  function deleteTodo(todo) {
    console.log("longpress");
    Alert.alert("Delete note", "Are you sure you want to delete this todo ?", [
      {
        text: "Delete",
        style: "destructive",
        onPress: () => {
          console.log("Delete this todo", todo);
        },
      },
    ]);
  }

  function renderTodoList() {
    return getFileteredList().map((todo) => (
      <View key={todo.id} style={s.cardItem}>
        <CardToDO onLongPress={deleteTodo} onPress={updateTodo} todo={todo} />
      </View>
    ));
  }

  function updateTodo(todo) {
    const updatedTodo = {
      ...todo,
      isCompleted: !todo.isCompleted,
    };
    const updatedTodoList = [...todoList];
    const indextoUpdate = updatedTodoList.findIndex(
      (t) => t.id === updatedTodo.id
    );
    updatedTodoList[indextoUpdate] = updatedTodo;
    setTodoList(updatedTodoList);
  }

  return (
    <>
      <SafeAreaProvider>
        <SafeAreaView style={s.app}>
          <View style={s.header}>
            <Header />
          </View>
          <View style={s.body}>
            <ScrollView>{renderTodoList()}</ScrollView>
          </View>
        </SafeAreaView>
      </SafeAreaProvider>
      <View style={s.footer}>
        <TabBottomMenu
          todoList={todoList}
          onPress={setSelectedTabName}
          selectedTabName={selectedTabName}
        />
      </View>
    </>
  );
}
