import { Text, TouchableOpacity, View } from "react-native-web";
import { s } from "./TabBottomMenu.style";

export function TabBottomMenu({ selectedTabName }) {
  function getTextStyle(tabName) {
    return {
      fontWeight: "bold",
      color: selectedTabName === tabName ? "#2F76E5" : "black",
    };
  }

  return (
    <View style={s.root}>
      <TouchableOpacity>
        <Text style={getTextStyle("all")}>All</Text>
      </TouchableOpacity>
      <TouchableOpacity>
        <Text style={getTextStyle("progress")}>Progress</Text>
      </TouchableOpacity>
      <TouchableOpacity>
        <Text style={getTextStyle("done")}>Done</Text>
      </TouchableOpacity>
    </View>
  );
}
